import React, { useState, useEffect } from 'react';
import ContainerStyle from './containerStyles';

const Container = () => {
  const [stock, setStock] = useState([]);

  useEffect(() => {
    const getFriends = async () => {
      let r = await fetch('/data/products.json');
      let stock = await r.json();
      setStock(stock);
    };
    getFriends();
  }, []);

  const myData = []
    .concat(stock)
    .sort((a, b) => (a.sales_ranking > b.sales_ranking ? 1 : -1))
    .map((product, index) => (
      <div key={index} className="card">
        <div className="overlay">
          <button>Mark Complete</button>
        </div>
        <div
          className="img"
          style={{ backgroundImage: `url(/images/${product.code}.jpg)` }}
        >
          <span>{product.sales_ranking}</span>
        </div>
        <div className="wrap">
          <div className="code">{product.code}</div>
          <div className="name">{product.name}</div>
          <div className="price">{product.price}â‚¬</div>
          <div className="chat"></div>
          <div className="stockout">
            <div className="percentage">
              <span className="count">25</span>
              <span className="percent">%</span>
              <span className="label">Stockout</span>
            </div>
            <div className="bar-wrap">
              <div className="bar" style={{ width: '25%' }}></div>
            </div>
          </div>
          <div className="coverage">
            <div className="title bad"> Very Low</div>
            <div className="caption">WH COVERAGE</div>
          </div>
        </div>
      </div>
    ));

  return (
    <ContainerStyle>
      {stock.map(
        (product, index) =>
          myData
