import styled from 'styled-components';

const ContainerStyle = styled.div`
  width: 70%;
  height: 100%;
  margin: auto;
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  margin-top: 50px;
  @media (max-width: 991px) {
    width: 90%;
    margin-top: 20px;
  }
`;

export default ContainerStyle;
