import React from 'react';
import ReactDOM from 'react-dom';
import { ThemeProvider, createGlobalStyle } from 'styled-components';
import App from './App';

const theme = {
  headerHeight: '8rem',
  brandColor: '#c6ae36',
  brandColor2: '#4d80bf',
  black: '#051441',
  white: '#ffffff',
  captionFont: '1.8rem',
  titleFont: '3rem',
  footerColor: '#252d34'
};

const GlobalStyle = createGlobalStyle`
  html {
    box-sizing: border-box;
    font-size: 10px;
  }
  *, *:before, *:after {
    box-sizing: inherit;
  }
  body {
    padding: 0;
    margin: 0;
    font-size: 1.5rem;
    line-height: 2;
    font-family: 'Roboto', sans-serif;
    color: #051441;
  }
  a {
    text-decoration: none;
    color: ${theme.black};
  }

  .hide-on-mobile{
      display: inline-block!important;
      @media (max-width: 991px){
          display: none!important;
      }
  }
  .hide-on-desktop{
      display: none!important;
      @media (max-width: 991px){
          display: inline-block!important;
      }
  }
`;

ReactDOM.render(
  <ThemeProvider theme={theme}>
    <React.StrictMode>
      <App />
    </React.StrictMode>
  </ThemeProvider>,
  document.getElementById('root')
);
