import React from 'react';
import ReactDOM from 'react-dom';
import { ThemeProvider, createGlobalStyle } from 'styled-components';
import App from './App';

const theme = {
  lightRed: '#f95f60',
  white: '#ffffff',
  green: '#a0d468',
  red: '#da3f52',
  lightGrey: '#97a2b0',
  grey: '#95a0ae',
  textColor: '#464b56',
  borderColor: '#e2e2e2'
};

const GlobalStyle = createGlobalStyle`
  html {
    box-sizing: border-box;
    font-size: 10px;
  }
  *, *:before, *:after {
    box-sizing: inherit;
  }
  body {
    padding: 0;
    margin: 0;
    font-size: 1.5rem;
    line-height: 2;
    font-family: 'Roboto', sans-serif;
    background-color: #f4f7fa;
  }
  .container{
    width: 70%;
    height: 100%;
    margin: auto;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    margin-top: 50px;
    @media (max-width: 991px) {
      width: 90%;
      margin-top: 20px;
    }
  }
  a, button{
    text-decoration: none;
    outline: none;
  }
`;

ReactDOM.render(
  <ThemeProvider theme={theme}>
    <React.StrictMode>
      <GlobalStyle />
      <App />
    </React.StrictMode>
  </ThemeProvider>,
  document.getElementById('root')
);
